mapper.insert:
INSERT
    INTO TBL_ORDER
(
    ORDER_DATE
  , ORDER_TIME
  , TOTAL_ORDER_PRICE
)
VALUES
(
    #{orderDate}
  , #{orderTime}
  , #{totalOrderPrice}
)

    SELECT MAX(ORDER_CODE) FROM TBL_ORDER

INSERT
    INTO TBL_ORDER_MENU
   (
    ORDER_CODE
  , MENU_CODE
  , ORDER_AMOUNT
)
VALUES
(
    #{orderCode}
  , #{menuCode}
  , #{orderAmount}
)
